mapper.update:
UPDATE attendance SET
	attendance_record='출석'
WHERE schedule_no = #{scheduleNo};

mapper.select:
SELECT a.attendance_record attendanceRecord , sc.schedule_no scheduleNo , st.student_name studentName , sc.schedule_date scheduleDate , l.lecture_name lectureName , e.login_id loginId FROM attendance a JOIN education e ON a.education_no = e.education_no JOIN lecture l ON e.lecture_name = l.lecture_name JOIN schedule sc ON sc.schedule_no = a.schedule_no JOIN student st ON st.login_id = e.login_id WHERE l.teacher = #{loginId} AND l.lecture_active = '1'
AND sc.schedule_date = #{scheduleDate}
AND sc.schedule_date = CURDATE()

mapper.update:
UPDATE attendance SET
	attendance_record = #{attendanceRecord}
WHERE schedule_no = #{scheduleNo} AND education_no = #{educationNo}
