mapper.insert:
INSERT INTO lecture(
	lecture_name
	, teacher
	, manager
	, lecture_start_date
	, lecture_end_date
	, lecture_room_name
	, lecture_student_capacity
	, login_id
	, lecture_active
	, create_date
	, update_date)
VALUES(
	#{lectureName}
	, #{teacher}
	, #{manager}
	, #{lectureStartDate}
	, #{lectureEndDate}
	, #{lectureRoomName}
	, #{lectureStudentCapacity}
	, #{loginId}
	, '0'
	, NOW()
	, NOW()
	)

mapper.update:
UPDATE lecture
SET lecture_active = '1'
WHERE lecture_name = #{lectureName}

mapper.insert:
INSERT INTO lecture_subject(
	lecture_name
	, subject_name
)
VALUES(
	#{lectureName}
	, #{subjectName}
)

mapper.update:
UPDATE lecture
SET 
	teacher = #{teacher}
	, manager = #{manager}
	, lecture_start_date = #{lectureStartDate}
	, lecture_end_date = #{lectureEndDate}
	, lecture_room_name = #{lectureRoomName}
	, lecture_student_capacity = #{lectureStudentCapacity}
WHERE lecture_name = #{lectureName}

UPDATE schedule SET 
	schedule_date = #{scheduleDate}
	,lecture_subject_no = #{lectureSubjectNo}
	,update_date = NOW() 
WHERE schedule_no = #{scheduleNo}

mapper.select:
SELECT COUNT(*)
FROM lecture
WHERE lecture_name = #{lectureName}

mapper.update:

