mapper.update:
			UPDATE manager SET
 				manager_name managerName= #{managerName}	
				, manager_birth managerBirth= #{managerBirth}	
      			, manager_gender managerGender= #{managerGender}	
      			, address= #{address}	
     			, detail_addr detailAddr= #{detailAddr}	
      			, manager_email managerEmail= #{managerEmail}	
      			, manager_phone managerPhone= #{managerPhone}	
				, dept_no deptNo= #{deptNo}	
				, position_no positionNo= #{positionNo}	
		WHERE login_id loginId= #{loginId}
