mapper.update:
		UPDATE manager SET
			manager_name = #{managerName}	
			, manager_birth = #{managerBirth}	
   			, manager_gender = #{managerGender}	
   			, address= #{address}	
  			, detail_addr = #{detailAddr}	
   			, manager_email = #{managerEmail}	
   			, manager_phone = #{managerPhone}	
			, dept_no = #{deptNo}	
			, position_no = #{positionNo}	
		WHERE login_id = #{loginId}
