mapper.update:
UPDATE student
SET student_name = #{studentName}
	, student_gender = #{studentGender}
	, address = #{address}
	, detail_address = #{detailAddress}
	, student_email = #{studentEmail}
	, student_phone = #{studentPhone}
	, military_status = #{militaryStatus}
	, graduate = #{graduate}
	, update_date = NOW()
WHERE login_id = #{loginId}
